package arrayshitexercise;

public class Oddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int [] x= {10, 21, 303, 50, 34, 409, 20, 11, 33,100};
		int z=x.length;
		for(int a=0;a<z;a=a+1) {
		if(x[a]%2==1) {
			System.out.println(x[a]);
		}
			
		}
	}

}
