package Conditionalstatementexercise;

public class Ifelse3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=9;
		int y=20;
		if(x>y) {
		System.out.println("x is bigger");
	}
		else if(x<y) {
			System.out.println("y is bigger");
		}

}
}
