package classreview;

public class Conditionalstatementg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=30;
		int y=10;
		int z=400;
		
		if(x>=y&&x>=z) {
			System.out.println(x +"is bigger");
		}
			else if(x<=y&&x<=z) {
				System.out.println(y+"is bigger");
			}
			else {
				System.out.println(z+":is bigger");
			}
		}
	}


