package exersize6;

public class Ifelseelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10;
		if(x>=0) {
			System.out.println("x is positive");
		}
		else if(x<=0){
			System.out.println("x is negetive");
		}
		else {
			System.out.println("x is zero");
		}
	}

}
