package stringexercise;

public class Star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int x=0;x<15;x++) {
			if(x==1||x==3||x==6||x==10) {
			System.out.println("\n");
			System.out.println("*");
			}
			else {
				System.out.println("*");
			}
		}
	}}
